import React, { useState } from 'react';
import './App.css';
import CartContext from "./components/CartContext"
import { Navegador } from './components/Navegador';
import { Hero } from './components/Hero';
import { Nosotros } from './components/Nosotros';
import { Contacto } from './components/Contacto';
import { Footer } from './components/Footer';
import { Menu } from './components/Menu';



const XIcon = () => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    <line x1="18" y1="6" x2="6" y2="18"/><line x1="6" y1="6" x2="18" y2="18"/>
  </svg>
);

const PlusIcon = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    <line x1="12" y1="5" x2="12" y2="19"/><line x1="5" y1="12" x2="19" y2="12"/>
  </svg>
);

const MinusIcon = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    <line x1="5" y1="12" x2="19" y2="12"/>
  </svg>
);

const TrashIcon = () => (
  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    <polyline points="3 6 5 6 21 6"/><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"/>
  </svg>
);

function App() {
  const [cartOpen, setCartOpen] = useState(false);
  const [cart, setCart] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('todos');

  const products = {
    pizzas: [
      { id: 1, name: 'Pizza Margarita', price: 8500, description: 'Salsa de tomate, mozzarella fresca y albahaca', image: 'ðŸ•', category: 'pizzas' },
      { id: 2, name: 'Pizza Pepperoni', price: 9500, description: 'Pepperoni, mozzarella y orÃ©gano', image: 'ðŸ•', category: 'pizzas' },
      { id: 3, name: 'Pizza Cuatro Quesos', price: 10500, description: 'Mozzarella, gorgonzola, parmesano y provolone', image: 'ðŸ•', category: 'pizzas' },
      { id: 4, name: 'Pizza Hawaiana', price: 9000, description: 'JamÃ³n, piÃ±a y mozzarella', image: 'ðŸ•', category: 'pizzas' }
    ],
    sandwiches: [
      { id: 5, name: 'Sandwich ClÃ¡sico', price: 5500, description: 'JamÃ³n, queso, lechuga y tomate', image: 'ðŸ¥ª', category: 'sandwiches' },
      { id: 6, name: 'Sandwich Vegetariano', price: 6000, description: 'Vegetales grillados, hummus y rÃºcula', image: 'ðŸ¥ª', category: 'sandwiches' },
      { id: 7, name: 'Sandwich de Pollo', price: 6500, description: 'Pollo grillado, queso cheddar y salsa BBQ', image: 'ðŸ¥ª', category: 'sandwiches' },
      { id: 8, name: 'Sandwich Completo', price: 7000, description: 'Carne, huevo, queso, lechuga y tomate', image: 'ðŸ¥ª', category: 'sandwiches' }
    ],
    wraps: [
      { id: 9, name: 'Wrap CÃ©sar', price: 6500, description: 'Pollo, lechuga romana, parmesano y aderezo cÃ©sar', image: 'ðŸŒ¯', category: 'wraps' },
      { id: 10, name: 'Wrap Mexicano', price: 7000, description: 'Carne, frijoles, guacamole y pico de gallo', image: 'ðŸŒ¯', category: 'wraps' },
      { id: 11, name: 'Wrap Vegano', price: 6500, description: 'Falafel, hummus, vegetales frescos', image: 'ðŸŒ¯', category: 'wraps' },
      { id: 12, name: 'Wrap BBQ', price: 7500, description: 'Pollo BBQ, cebolla caramelizada y queso', image: 'ðŸŒ¯', category: 'wraps' }
    ]
  };

  const allProducts = [...products.pizzas, ...products.sandwiches, ...products.wraps];

  const filteredProducts = selectedCategory === 'todos' 
    ? allProducts 
    : allProducts.filter(p => p.category === selectedCategory);

  const addToCart = (product) => {
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      setCart(cart.map(item => 
        item.id === product.id 
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  const updateQuantity = (productId, delta) => {
    setCart(cart.map(item => {
      if (item.id === productId) {
        const newQuantity = item.quantity + delta;
        return newQuantity > 0 ? { ...item, quantity: newQuantity } : item;
      }
      return item;
    }).filter(item => item.quantity > 0));
  };

  const getTotal = () => {
    return cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
  };

  const getTotalItems = () => {
    return cart.reduce((sum, item) => sum + item.quantity, 0);
  };

  return (
    <div className="app">
      
      <Navegador />

      <Hero/>

      <Menu/>
          

      <Nosotros/>

      <Contacto/>


      <Footer/>

      {/* Shopping Cart Sidebar */}
      {cartOpen && (
        <div className="cart-overlay">
          <div className="cart-backdrop" onClick={() => setCartOpen(false)} />
          <div className="cart-sidebar">
            <div className="cart-header">
              <h2 className="cart-title">Tu Carrito</h2>
              <button onClick={() => setCartOpen(false)} className="close-cart-btn">
                <XIcon />
              </button>
            </div>

            <div className="cart-content">
              {cart.length === 0 ? (
                <div className="cart-empty">
                  <div className="cart-empty-icon">ðŸ›’</div>
                  <p className="cart-empty-text">Tu carrito estÃ¡ vacÃ­o</p>
                </div>
              ) : (
                <div className="cart-items">
                  {cart.map(item => (
                    <div key={item.id} className="cart-item">
                      <div className="cart-item-header">
                        <div className="cart-item-info">
                          <div className="cart-item-icon">{item.image}</div>
                          <div>
                            <h3 className="cart-item-name">{item.name}</h3>
                            <p className="cart-item-price">
                              ${item.price.toLocaleString()}
                            </p>
                          </div>
                        </div>
                        <button
                          onClick={() => removeFromCart(item.id)}
                          className="remove-item-btn"
                        >
                          <TrashIcon />
                        </button>
                      </div>
                      <div className="cart-item-footer">
                        <div className="quantity-controls">
                          <button
                            onClick={() => updateQuantity(item.id, -1)}
                            className="quantity-btn"
                          >
                            <MinusIcon />
                          </button>
                          <span className="quantity-value">{item.quantity}</span>
                          <button
                            onClick={() => updateQuantity(item.id, 1)}
                            className="quantity-btn"
                          >
                            <PlusIcon />
                          </button>
                        </div>
                        <span className="cart-item-total">
                          ${(item.price * item.quantity).toLocaleString()}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {cart.length > 0 && (
              <div className="cart-footer">
                <div className="cart-total">
                  <span>Total:</span>
                  <span className="cart-total-amount">${getTotal().toLocaleString()}</span>
                </div>
                <button className="checkout-btn">
                  Finalizar Compra
                </button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
