import React, { use } from 'react'
import { useState } from 'react'
import { ShoppingCart, XIcon, MenuIcon, Hamburger } from 'lucide-react'
import "./Navegador.css"

export const Navegador = () => {
    const[carritoOpen, setCarritoOpen] = useState(false)
    const[menuOpen, setMenuOpen] = useState(false)  

    const handleLinkClick = () =>{
      setMenuOpen(false)
    }

    const addToCart = (product) => {
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      setCart(cart.map(item => 
        item.id === product.id 
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  const updateQuantity = (productId, delta) => {
    setCart(cart.map(item => {
      if (item.id === productId) {
        const newQuantity = item.quantity + delta;
        return newQuantity > 0 ? { ...item, quantity: newQuantity } : item;
      }
      return item;
    }).filter(item => item.quantity > 0));
  };

  const getTotal = () => {
    return cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
  };

  const getTotalItems = () => {
    return cart.reduce((sum, item) => sum + item.quantity, 0);
  };

  return (

    <header>
        <div className='logo-container'>
            <Hamburger></Hamburger>
            <p>Bar & Grill</p>
        </div>

        <nav>
            <ul className='nav-ul'>
                <li><a href='#inicio' className='nav-li'>Inicio</a></li>
                <li><a href='#menu' className='nav-li'>Menu</a></li>
                <li><a href='#nosotros' className='nav-li'>Nosotros</a></li>
                <li><a href='#contacto' className='nav-li'>Contacto</a></li>
            </ul>
        </nav>

        <div className="actions-container">
            <button className='carrito-button'><ShoppingCart></ShoppingCart></button>
            <button onClick={() => setMenuOpen(!menuOpen)} className='menu-button'>
              {menuOpen ? <XIcon /> : <MenuIcon />}
            </button>
        </div>
        
        {/* Navegador Responsive */}
        {menuOpen && (
            <nav className="nav-mobile">
              <ul className='nav-ul'>
                <li><a href='#inicio' className='nav-li' onClick={handleLinkClick}>Inicio</a></li>
                <li><a href='#menu' className='nav-li' onClick={handleLinkClick}>Menu</a></li>
                <li><a href='#nosotros' className='nav-li' onClick={handleLinkClick}>Nosotros</a></li>
                <li><a href='#contacto' className='nav-li' onClick={handleLinkClick}>Contacto</a></li>
              </ul>
                
            </nav>
          )}

          {cartOpen && (
        <div className="cart-overlay">
          <div className="cart-backdrop" onClick={() => setCartOpen(false)} />
          <div className="cart-sidebar">
            <div className="cart-header">
              <h2 className="cart-title">Tu Carrito</h2>
              <button onClick={() => setCartOpen(false)} className="close-cart-btn">
                <XIcon />
              </button>
            </div>

            <div className="cart-content">
              {cart.length === 0 ? (
                <div className="cart-empty">
                  <div className="cart-empty-icon">ðŸ›’</div>
                  <p className="cart-empty-text">Tu carrito estÃ¡ vacÃ­o</p>
                </div>
              ) : (
                <div className="cart-items">
                  {cart.map(item => (
                    <div key={item.id} className="cart-item">
                      <div className="cart-item-header">
                        <div className="cart-item-info">
                          <div className="cart-item-icon">{item.image}</div>
                          <div>
                            <h3 className="cart-item-name">{item.name}</h3>
                            <p className="cart-item-price">
                              ${item.price.toLocaleString()}
                            </p>
                          </div>
                        </div>
                        <button
                          onClick={() => removeFromCart(item.id)}
                          className="remove-item-btn"
                        >
                          <TrashIcon />
                        </button>
                      </div>
                      <div className="cart-item-footer">
                        <div className="quantity-controls">
                          <button
                            onClick={() => updateQuantity(item.id, -1)}
                            className="quantity-btn"
                          >
                            <MinusIcon />
                          </button>
                          <span className="quantity-value">{item.quantity}</span>
                          <button
                            onClick={() => updateQuantity(item.id, 1)}
                            className="quantity-btn"
                          >
                            <PlusIcon />
                          </button>
                        </div>
                        <span className="cart-item-total">
                          ${(item.price * item.quantity).toLocaleString()}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {cart.length > 0 && (
              <div className="cart-footer">
                <div className="cart-total">
                  <span>Total:</span>
                  <span className="cart-total-amount">${getTotal().toLocaleString()}</span>
                </div>
                <button className="checkout-btn">
                  Finalizar Compra
                </button>
              </div>
            )}
          </div>
        </div>
      )}

    </header>
  )
}
