import { useState, useEffect } from 'react';
import './MisPedidos.css';

export const MisPedidos = () => {
  const [pedidos, setPedidos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Cargar pedidos al montar el componente
  useEffect(() => {
    fetchPedidos();
  }, []);

  const fetchPedidos = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:8000/pedidos');
      
      if (!response.ok) {
        throw new Error('Error al cargar los pedidos');
      }
      
      const data = await response.json();
      setPedidos(data);
    } catch (err) {
      setError(err.message);
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const cancelarPedido = async (pedidoId) => {
    if (!confirm('¬øEst√°s seguro de que deseas cancelar este pedido?')) {
      return;
    }

    try {
      const response = await fetch(`http://localhost:8000/pedidos/${pedidoId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Error al cancelar el pedido');
      }

      const result = await response.json();
      alert(result.mensaje);

      // Actualizar la lista de pedidos
      fetchPedidos();
    } catch (err) {
      alert('Error: ' + err.message);
      console.error('Error:', err);
    }
  };

  if (loading) {
    return (
      <section className="mis-pedidos-section">
        <div className="container">
          <h2 className="section-title">Mis Pedidos</h2>
          <div className="loading">
            <div className="spinner"></div>
            <p>Cargando pedidos...</p>
          </div>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="mis-pedidos-section">
        <div className="container">
          <h2 className="section-title">Mis Pedidos</h2>
          <div className="error">
            <p>Error: {error}</p>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="mis-pedidos-section" id='mispedidos'>
      <div className="container">
        <h2 className="section-title">Mis Pedidos</h2>
        
        {pedidos.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">üì¶</div>
            <p>No tienes pedidos realizados</p>
          </div>
        ) : (
          <div className="pedidos-grid">
            {pedidos.map((pedido) => (
              <div key={pedido.pedido_id} className="pedido-card">
                {/* Header del pedido */}
                <div className="pedido-header">
                  <div className="pedido-info">
                    <span className="pedido-numero">
                      Pedido #{pedido.pedido_id}
                    </span>
                    <span className="pedido-fecha">
                      {new Date(pedido.fecha_pedido).toLocaleDateString('es-AR', {
                        day: 'numeric',
                        month: 'long',
                        year: 'numeric',
                        hour: '2-digit',
                        minute: '2-digit'
                      })}
                    </span>
                  </div>
                  <span className="pedido-estado">Entregado</span>
                </div>

                {/* Datos del cliente */}
                <div className="pedido-cliente">
                  <div className="cliente-row">
                    <span className="cliente-label">Cliente:</span>
                    <span className="cliente-value">{pedido.nombre_cliente}</span>
                  </div>
                  <div className="cliente-row">
                    <span className="cliente-label">Direcci√≥n:</span>
                    <span className="cliente-value">{pedido.direccion}</span>
                  </div>
                </div>

                {/* Items del pedido */}
                {pedido.items && pedido.items.length > 0 && (
                  <div className="pedido-items">
                    <h4 className="items-title">Productos:</h4>
                    {pedido.items.map((item, index) => (
                      <div key={index} className="item-row">
                        <span className="item-emoji">
                          {item.categoria === 'pizza' ? 'üçï' : 
                           item.categoria === 'sandwich' ? 'ü•™' : 'üåØ'}
                        </span>
                        <span className="item-info">
                          {item.cantidad}x {item.nombre}
                        </span>
                        <span className="item-precio">
                          ${(item.precio_unitario * item.cantidad).toLocaleString()}
                        </span>
                      </div>
                    ))}
                  </div>
                )}

                {/* Footer con total y bot√≥n */}
                <div className="pedido-footer">
                  <div className="pedido-total">
                    <span>Total:</span>
                    <span className="total-amount">
                      ${pedido.total.toLocaleString()}
                    </span>
                  </div>
                  <button
                    onClick={() => cancelarPedido(pedido.pedido_id)}
                    className="cancel-pedido-btn"
                  >
                    ‚úï Cancelar Pedido
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </section>
  );
};
